import React, { useState } from "react";
import "./Footer.css";
import { ToastContainer, toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";

const Footer = () => {
  const [email, setEmail] = useState("");
  const [error, setError] = useState("");

  const handleChange = (event) => {
    const value = event.target.value;
    setEmail(value);

    if (value.includes(".com" && "@")) {
      setError("");
    } else {
      setError("! Please enter a valid email");
    }
  };

  const handleSubscribed = (e) => {
    e.preventDefault();
    if (email.trim() !== "" && !error) {
      toast.success("Subscribed successfully");
    } else {
      console.log("Valid email is required to subscribe");
      toast.error("Please enter a valid email");
    }
  };

  const onSubmit = async (event) => {
    event.preventDefault();
    const formData = new FormData(event.target);
    formData.append("access_key", "abf6f513-c6e6-4a94-8375-3c087c25ee4c");

    try {
      const response = await fetch("https://api.web3forms.com/submit", {
        method: "POST",
        body: formData,
      });

      const data = await response.json();

      if (data.success) {
        event.target.reset();
      } else {
        console.log("Error", data);
      }
    } catch (error) {
      console.error("Error submitting form:", error);
    }
  };

  return (
    <div className="footer">
      <div className="footer-top">
        <div className="footer-top-left">
          <img src="./img/logo.png" alt="Logo" />
          <p>I am a frontend developer from Pune with 1+ years of experience</p>
        </div>
        <div className="footer-top-right">
          <form onSubmit={onSubmit} className="footer-email-form">
            <input
              className="footer-email-input"
              type="text"
              placeholder="Enter your email address here..."
              value={email}
              onChange={handleChange}
              required
            />
            <button onClick={handleSubscribed} className="footer-subscribe">
              Subscribe
            </button>
            <ToastContainer />
          </form>
        </div>
      </div>
      <hr />
      <div className="footer-bottom">
        <div className="footer-bottom-left">
          <div className="img"></div>
          <a href="https://www.linkedin.com/in/aadibkazi/">
            <img src="./img/linkedin.png" alt="LinkedIn" />
          </a>
          <a href="https://www.instagram.com/aadib____/">
            <img src="./img/instagram.png" alt="Instagram" />
          </a>
          <a href="https://github.com/Aadibkai">
            <img src="./img/github.png" alt="GitHub" />
          </a>
        </div>
        <div className="footer-bottom-right">
          <p>Terms of Service</p>
          <p>Privacy Policy</p>
          <p>Connect with me</p>
        </div>
      </div>
    </div>
  );
};

export default Footer;
