import React, { useState } from "react";
import "./Footer.css";
import { ToastContainer, toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";

const Footer = () => {
  const [email, setEmail] = useState("");
  const [isSubscribed, setIsSubscribed] = useState(false);

  const handleChange = (event) => {
    setEmail(event.target.value);
  };

  const handleSubscribe = () => {
    if (validateEmail(email)) {
      setIsSubscribed(true);
      toast.success("Subscribed successfully!");
    } else {
      toast.error("Please enter a valid email address!");
    }
  };

  const validateEmail = (email) => {
    const re =
      /^(([^<>()\[\]\\.,;:\s@"]+(\.[^<>()\[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/;
    return re.test(String(email).toLowerCase());
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    const formData = new FormData(event.target);
    formData.append("access_key", "abf6f513-c6e6-4a94-8375-3c087c25ee4c");

    try {
      const response = await fetch("https://api.web3forms.com/submit", {
        method: "POST",
        body: formData,
      });

      const data = await response.json();

      if (data.success) {
        toast.success("Form submitted successfully!");
        event.target.reset();
      } else {
        toast.error("Error submitting form. Please try again later.");
      }
    } catch (error) {
      console.error("Error submitting form:", error);
      toast.error("Error submitting form. Please try again later.");
    }
  };

  return (
    <div className="footer">
      <div className="footer-top">
        <div className="footer-top-left">
          <img src="" alt="" />
          <p>I am a frontend developer from Pune with 1+ year of experience</p>
        </div>
        <div className="footer-top-right">
          <form onSubmit={handleSubmit} className="footer-email-form">
            <input
              type="email"
              placeholder="Enter your email address here..."
              value={email}
              onChange={handleChange}
              required
            />
            <button type="button" onClick={handleSubscribe}>
              Subscribe
            </button>
            <ToastContainer />
          </form>
        </div>
      </div>
      <hr />
      <div className="footer-bottom">
        <div className="social-links">
          <a href="https://www.linkedin.com/in/aadibkazi/">
            <img src="./img/linkedin.png" alt="LinkedIn" />
          </a>
          <a href="https://www.instagram.com/aadib____/">
            <img src="./img/instagram.png" alt="Instagram" />
          </a>
          <a href="https://github.com/Aadibkai">
            <img src="./img/github.png" alt="GitHub" />
          </a>
        </div>
        <div className="footer-bottom-right">
          <p>Term of services</p>
          <p>Privacy Policy</p>
          <p>Connect with me</p>
        </div>
      </div>
    </div>
  );
};

export default Footer;
