import React from 'react';
import "./Footer.css";

const Footer = () => {
  const [result, setResult] = React.useState("");

  const onSubmit = async (event) => {
    event.preventDefault();
    setResult("Sending....");
    const formData = new FormData(event.target);

    formData.append("access_key", "abf6f513-c6e6-4a94-8375-3c087c25ee4c");

    try {
      const response = await fetch("https://api.web3forms.com/submit", {
        method: "POST",
        body: formData,
      });

      const data = await response.json();

      if (data.success) {
        setResult("Form Submitted Successfully");
        event.target.reset();
      } else {
        console.log("Error", data);
        setResult(data.message);
      }
    } catch (error) {
      console.log("Error", error);
      setResult("An error occurred. Please try again.");
    }
  };

  return (
    <div>
      <div className="footer">
        <div className="footer-top">
          <div className="footer-top-left">
            <img src="" alt="Logo" />
            <p>I am a frontend developer from Pune with 1+ year of experience.</p>
          </div>
          <div className="footer-top-right">
            <form onSubmit={onSubmit}>
              <div className="footer-email-input">
                <img style={{ height: "26px" }} src="./img/person.png" alt="Person Icon" />
                <input type="email" name="email" placeholder="Enter Your Email" required />
              </div>
              <button type="submit" className="footer-subscribe">Subscribe</button>
              {result && <p className="form-result">{result}</p>}
            </form>
          </div>
        </div>
        <hr />
        <div className="footer-bottom">
          <div className="footer-bottom-left">
            <p>&copy; 2025 Adib Kazi. All rights reserved.</p>
          </div>
          <div className="footer-bottom-right">
            <p>Terms of Service</p>
            <p>Privacy Policy</p>
            <p>Connect with me</p>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Footer;
